Job.Local:

public virtual void Local()
{
    Shell(@"Qizmt del regression_test_ByteSlice.Prepare_Input.txt", true);
    Shell(@"Qizmt del regression_test_ByteSlice.Prepare_Output.txt", true);    
}

Job.Remote:

  public virtual void Remote(RemoteInputStream dfsinput, RemoteOutputStream dfsoutput)
  {             
        //Create sample data.
        int stringLength = 2;
        int rowCount = 2000;
                       
        Random rnd = new Random(System.DateTime.Now.Millisecond / 2 + System.Diagnostics.Process.GetCurrentProcess().Id / 2);
    
        List<byte> onerow = new List<byte>();
        
        for(long rn = 0; rn < rowCount; rn++)
        {
            onerow.Clear();
            
            //string  
            for(int cnt = 0; cnt < stringLength; cnt++)
            {
                byte b = (byte)rnd.Next((int)' ' + 1, (int)'~' + 1);   
                
                onerow.Add(b); 
            }                          
           
            dfsoutput.WriteLine(onerow);        
        }     
   }

MapReduce.Map:
  
  public virtual void Map(ByteSlice line, MapOutput output)
  {
        output.Add(line, ByteSlice.Prepare());               
  }

MapReduce.Reduce:
  
  public override void Reduce(ByteSlice key, ByteSliceList values, ReduceOutput output)
  {                         
        string sOut = key.ToString();
        
        for(int i = 0; i < values.Length; i++)
        {
            string sVal = values.Items[i].ToString();
            ByteSlice b = ByteSlice.Prepare();
            output.Add(ByteSlice.Prepare(sOut+sVal+b.ToString()));
        }                        
  }

Job.Local:

public virtual void Local()
{
    //Compare input and output file, pass if they are the same.
    Shell(@"Qizmt exec regression_test_checkTestResult.xml regression_test_ByteSlice.Prepare_Input.txt regression_test_ByteSlice.Prepare_Output.txt regression_test_ByteSlice.Prepare.xml");          
    
    Shell(@"Qizmt del regression_test_ByteSlice.Prepare_Input.txt", true);
    Shell(@"Qizmt del regression_test_ByteSlice.Prepare_Output.txt", true); 
}
