Job.Local:

public virtual void Local()
{
    Shell(@"Qizmt del regression_test_Entry.AsciiToBytesAppend(StringBuilder_List)_Input.txt", true);
    Shell(@"Qizmt del regression_test_Entry.AsciiToBytesAppend(StringBuilder_List)_Output.txt", true);
}

Job.Remote:

  public virtual void Remote(RemoteInputStream dfsinput, RemoteOutputStream dfsoutput)
  {             
        //Prepare sample data.
        int stringLength = 500;
        int rowCount = 2000;
                       
        Random rnd = new Random(System.DateTime.Now.Millisecond / 2 + System.Diagnostics.Process.GetCurrentProcess().Id / 2);
    
        List<byte> onerow = new List<byte>();
        
        for(long rn = 0; rn < rowCount; rn++)
        {
            onerow.Clear();
            
            //string  
            for(int cnt = 0; cnt < stringLength; cnt++)
            {
                byte b = (byte)rnd.Next((int)' ' + 1, (int)'~' + 1);   
                
                onerow.Add(b); 
            }                          
           
            dfsoutput.WriteLine(onerow);        
        }     
   }

MapReduce.Map:
  
  public virtual void Map(ByteSlice line, MapOutput output)
  {
       string s = line.ToString();
       StringBuilder sb = new StringBuilder();
       sb.Append(s);
       List<byte> buf = new List<byte>();
       Entry.AsciiToBytesAppend(sb, buf);
       ByteSlice key = ByteSlice.Prepare(s.Substring(0,1));
       ByteSlice val = ByteSlice.Prepare(buf);
       output.Add(key, val);
  }

MapReduce.Reduce:
  
  public override void Reduce(ByteSlice key, ByteSliceList values, ReduceOutput output)
  {                      
        for(int i = 0; i < values.Length; i++)
        {
           string s = values.Items[i].ToString();
           StringBuilder sb = new StringBuilder();
           sb.Append(s);
           List<byte> buf = new List<byte>();
           Entry.AsciiToBytesAppend(sb, buf);
           ByteSlice val = ByteSlice.Prepare(buf);
           output.Add(val);
        }                        
  }

