Job.Local:

public virtual void Local()
{
    Shell(@"Qizmt del regression_test_combine_fail_filetypes_wildcard_Input.gensort");
    Shell(@"Qizmt del regression_test_combine_fail_filetypes_wildcard_Output.*");
}

Job.Remote:

    public virtual void Remote(RemoteInputStream dfsinput, RemoteOutputStream dfsoutput)
    {
        dfsoutput.WriteRecord(Encoding.UTF8.GetBytes("\0\nAB"));
        dfsoutput.WriteRecord(Encoding.UTF8.GetBytes("\0\nCD"));
        dfsoutput.WriteRecord(Encoding.UTF8.GetBytes("\0\nEF"));
    }


    public virtual void Remote(RemoteInputStream dfsinput, RemoteOutputStream dfsoutput)
    {
        dfsoutput.WriteRecord(Encoding.UTF8.GetBytes("\0\nAB"));
        dfsoutput.WriteRecord(Encoding.UTF8.GetBytes("\0\nCD"));
        dfsoutput.WriteRecord(Encoding.UTF8.GetBytes("\0\nEF"));
    }


    public virtual void Remote(RemoteInputStream dfsinput, RemoteOutputStream dfsoutput)
    {
        dfsoutput.WriteLine(Encoding.UTF8.GetBytes("GH"));
        dfsoutput.WriteLine(Encoding.UTF8.GetBytes("IJ"));
        dfsoutput.WriteLine(Encoding.UTF8.GetBytes("KL"));
        dfsoutput.WriteLine(Encoding.UTF8.GetBytes("MN"));
    }

Job.Local:

public virtual void Local()
{
    bool ok = false;
    try
    {
        Shell(@"Qizmt combine regression_test_combine_fail_filetypes_wildcard_Output.* + regression_test_combine_fail_filetypes_wildcard_Output.fail");
    }
    catch
    {
        ok = true;
    }
    if(!ok)
    {
        throw new Exception("Combine (binary record file + line file) did not fail as expected");
    }
}


public virtual void Local()
{
    Shell(@"Qizmt exec regression_test_iocompare.xml regression_test_combine_fail_filetypes_wildcard_Input.gensort regression_test_combine_fail_filetypes_wildcard_Output.gensort regression_test_combine_fail_filetypes_wildcard.xml");          
    
    Shell(@"Qizmt del regression_test_combine_fail_filetypes_wildcard_Input.gensort");
    Shell(@"Qizmt del regression_test_combine_fail_filetypes_wildcard_Output.*");
}
