Job.Local:

public virtual void Local()
{
    Shell(@"Qizmt del regression_test_invalidKeyMajor_Input.txt");
    Shell(@"Qizmt del regression_test_invalidKeyMajor_Output.txt");
    Shell(@"Qizmt gen regression_test_invalidKeyMajor_Input.txt 100B");
}

MapReduce.Map:
  
  public virtual void Map(ByteSlice line, MapOutput output)
  {                
        output.Add(line, ByteSlice.Prepare());
  }

MapReduce.Reduce:
  
  public override void Reduce(ByteSlice key, ByteSliceList values, ReduceOutput output)
  {                           
     
      for(int i = 0; i < values.Length; i++)
      {                  
            output.Add(key);                    
      }
  }
