Job.Local:

public virtual void Local()
{
    Shell(@"Qizmt del regression_test_mapreduce_output_grouped_small_Input.txt");
    Shell(@"Qizmt del regression_test_mapreduce_output_grouped_small_Output.txt");
}

Job.Remote:

    public virtual void Remote(RemoteInputStream dfsinput, RemoteOutputStream dfsoutput)
    {
        dfsoutput.WriteLine("samekey 1) a few lines");
        dfsoutput.WriteLine("samekey 2) testing");
        dfsoutput.WriteLine("samekey 3) a small mapreduce job!");
    }

MapReduce.Map:
  
  public virtual void Map(ByteSlice line, MapOutput output)
  {
      output.Add(ByteSlice.Prepare(line, 0, 4), line);
  }

MapReduce.Reduce:
  
  public override void Reduce(ByteSlice key, ByteSliceList values, ReduceOutput output)
  {
      IEnumerator<ByteSlice> evalues = values;
      while(evalues.MoveNext())
      {
          output.Add(evalues.Current);
      }
  }

Job.Local:

public virtual void Local()
{
    Shell(@"Qizmt exec regression_test_iocompare.xml regression_test_mapreduce_output_grouped_small_Input.txt regression_test_mapreduce_output_grouped_small_Output.txt regression_test_mapreduce_output_grouped_small.xml");          
    
    Shell(@"Qizmt del regression_test_mapreduce_output_grouped_small_Input.txt");
    Shell(@"Qizmt del regression_test_mapreduce_output_grouped_small_Output.txt");
}
