Job.Local:

public virtual void Local()
{
    Shell(@"Qizmt del regression_test_mstring.AppendM(char)_Input.txt", true);
    Shell(@"Qizmt del regression_test_mstring.AppendM(char)_Output.txt", true);    
}

Job.Remote:

  public virtual void Remote(RemoteInputStream dfsinput, RemoteOutputStream dfsoutput)
  {             
        //Create sample data.
        int stringLength = 1;
        int rowCount = 2000;
        char del = ',';
                       
        Random rnd = new Random(System.DateTime.Now.Millisecond / 2 + System.Diagnostics.Process.GetCurrentProcess().Id / 2);
    
        List<byte> onerow = new List<byte>();
        
        for(long rn = 0; rn < rowCount; rn++)
        {
            onerow.Clear();
            
            //string  
            int cnt = 0;
            while(cnt < stringLength)
            {
                byte b = (byte)rnd.Next((int)' ' + 1, (int)'~' + 1);   
                
                if(b != (byte)del)
                {
                    onerow.Add(b);   
                    cnt++;
                }
            }      
            
            //string  
            cnt = 0;
            while(cnt < stringLength)
            {
                byte b = (byte)rnd.Next((int)' ' + 1, (int)'~' + 1);   
                
                if(b != (byte)del)
                {
                    onerow.Add(b);   
                    cnt++;
                }
            }            
            
            dfsoutput.WriteLine(onerow);        
        }     
   }

MapReduce.Map:
  
  public virtual void Map(ByteSlice line, MapOutput output)
  {
        string sLine = line.ToString(); 
        char c = sLine[0];
        char c2 = sLine[1];
        mstring val = mstring.Prepare();
        val = val.AppendM(c).AppendM(c2);
        output.Add(mstring.Prepare(c), val);               
  }

MapReduce.Reduce:
  
  public override void Reduce(ByteSlice key, ByteSliceList values, ReduceOutput output)
  {                          
        for(int i = 0; i < values.Length; i++)
        {
            string sLine = values.Items[i].ToString(); 
            char c = sLine[0];
            char c2 = sLine[1];
            mstring val = mstring.Prepare();
            val = val.AppendM(c).AppendM(c2);
            output.Add(val);                    
        }                        
  }

Job.Local:

public virtual void Local()
{
    //Compare input and output file, pass if they are the same.
    Shell(@"Qizmt exec regression_test_checkTestResult.xml regression_test_mstring.AppendM(char)_Input.txt regression_test_mstring.AppendM(char)_Output.txt regression_test_mstring.AppendM(char).xml");          
    
    Shell(@"Qizmt del regression_test_mstring.AppendM(char)_Input.txt", true);
    Shell(@"Qizmt del regression_test_mstring.AppendM(char)_Output.txt", true); 
}
