Job.Local:

public virtual void Local()
{
    Shell(@"Qizmt del regression_test_mstring.Consume(double)_Input.txt", true);
    Shell(@"Qizmt del regression_test_mstring.Consume(double)_Output.txt", true);    
}

Job.Remote:

  public virtual void Remote(RemoteInputStream dfsinput, RemoteOutputStream dfsoutput)
  {             
        //Create sample data.
        int rowCount = 2000;
        char del = ',';
                       
        Random rnd = new Random(System.DateTime.Now.Millisecond / 2 + System.Diagnostics.Process.GetCurrentProcess().Id / 2);
    
        List<byte> onerow = new List<byte>();
        
        for(long rn = 0; rn < rowCount; rn++)
        {
            onerow.Clear();
            
            //label
            string s = rn.ToString();
            byte[] buf = System.Text.Encoding.UTF8.GetBytes(s);
            
            foreach(byte b in buf)
            {
                onerow.Add(b);
            }
            
            onerow.Add((byte)':');
            
            //double  
            double d = rnd.NextDouble();
            
            if(rnd.Next() % 2 == 1)
            {
                d = d * -1;
            }
            
            s = d.ToString();
            buf = System.Text.Encoding.UTF8.GetBytes(s);
            
            foreach(byte b in buf)
            {
                onerow.Add(b);
            }
            
            onerow.Add((byte)del);
            
             //double  
            d = rnd.NextDouble();
            
             if(rnd.Next() % 2 == 1)
            {
                d = d * -1;
            }
            
            s = d.ToString();
            buf = System.Text.Encoding.UTF8.GetBytes(s);
            
            foreach(byte b in buf)
            {
                onerow.Add(b);
            }
            
            dfsoutput.WriteLine(onerow);        
        }     
   }

MapReduce.Map:
  
  public virtual void Map(ByteSlice line, MapOutput output)
  {
        string sLine = line.ToString();
        int col = sLine.IndexOf(':');
        string label = sLine.Substring(0, col + 1);
        
        int del = sLine.IndexOf(',');
        double d = double.Parse(sLine.Substring(col + 1, del - col - 1)); 
        double d2 = double.Parse(sLine.Substring(del + 1));
        
        int k = (int)(Math.Round(d * 100));
        string key = k.ToString();
        
        char c = ',';
        
        mstring val = mstring.Prepare();
        val = val.Consume(ref label).Consume(ref d).Consume(ref c).Consume(ref d2);
        output.Add(mstring.Prepare(key), val);               
  }

MapReduce.Reduce:
  
  public override void Reduce(ByteSlice key, ByteSliceList values, ReduceOutput output)
  {                         
        for(int i = 0; i < values.Length; i++)
        {
            string sLine = values.Items[i].ToString();
            int col = sLine.IndexOf(':');
            string label = sLine.Substring(0, col + 1);
            
            int del = sLine.IndexOf(',');
            double d = double.Parse(sLine.Substring(col + 1, del - col - 1)); 
            double d2 = double.Parse(sLine.Substring(del + 1));
            
            char c = ',';
            
            mstring val = mstring.Prepare();
            val = val.Consume(ref label).Consume(ref d).Consume(ref c).Consume(ref d2);
            output.Add(val);             
        }                        
  }

