public virtual void Local()
{
    //Compare input and output file, pass if they are the same.
    Shell(@"Qizmt exec regression_test_checkTestResult.xml regression_test_mstring.Consume(double)_Input.txt regression_test_mstring.Consume(double)_Output.txt regression_test_mstring.Consume(double).xml double");          
    
    Shell(@"Qizmt del regression_test_mstring.Consume(double)_Input.txt", true);
    Shell(@"Qizmt del regression_test_mstring.Consume(double)_Output.txt", true); 
}
