Job.Local:

public virtual void Local()
{
    Shell(@"Qizmt del regression_test_mstring.CsvNextItemToDouble_Input.txt", true);
    Shell(@"Qizmt del regression_test_mstring.CsvNextItemToDouble_Output.txt", true);    
}

Job.Remote:

  public virtual void Remote(RemoteInputStream dfsinput, RemoteOutputStream dfsoutput)
  {             
        //Create sample data.
        int rowCount = 2000;
        char del = ',';
                       
        Random rnd = new Random(System.DateTime.Now.Millisecond / 2 + System.Diagnostics.Process.GetCurrentProcess().Id / 2);
    
        List<byte> onerow = new List<byte>();
        
        for(long rn = 0; rn < rowCount; rn++)
        {
            onerow.Clear();
            
            //label
            string s = rn.ToString();
            byte[] buf = System.Text.Encoding.UTF8.GetBytes(s);
            
            foreach(byte b in buf)
            {
                onerow.Add(b);
            }
            
            onerow.Add((byte)':');
            
            //double  
            double d = rnd.NextDouble();
            
            if(rnd.Next() % 2 == 1)
            {
                d = d * -1;
            }
            
            s = d.ToString();
            buf = System.Text.Encoding.UTF8.GetBytes(s);
            
            foreach(byte b in buf)
            {
                onerow.Add(b);
            }
            
            onerow.Add((byte)del);
            
             //double  
            d = rnd.NextDouble();
            
             if(rnd.Next() % 2 == 1)
            {
                d = d * -1;
            }
            
            s = d.ToString();
            buf = System.Text.Encoding.UTF8.GetBytes(s);
            
            foreach(byte b in buf)
            {
                onerow.Add(b);
            }
            
            dfsoutput.WriteLine(onerow);        
        }     
   }

MapReduce.Map:
  
  public virtual void Map(ByteSlice line, MapOutput output)
  {
        string t = line.ToString();                
        int col = t.IndexOf(':');
        string label = t.Substring(0, col + 1);
        
        mstring sLine = mstring.Prepare(t.Substring(col + 1));
        double d = sLine.CsvNextItemToDouble();  
        double d2 = sLine.CsvNextItemToDouble(); 
       
        int x = (int)Math.Round(d * 10);
        string key = x.ToString();
        
        mstring val = mstring.Prepare();
        val = val.AppendM(label).AppendM(d).AppendM(',').AppendM(d2);
        
        output.Add(mstring.Prepare(key), val);               
  }

MapReduce.Reduce:
  
  public override void Reduce(ByteSlice key, ByteSliceList values, ReduceOutput output)
  {                          
        for(int i = 0; i < values.Length; i++)
        {
            string t = values.Items[i].ToString();                
            int col = t.IndexOf(':');
            string label = t.Substring(0, col + 1);
            
            mstring sLine = mstring.Prepare(t.Substring(col + 1));
            double d = sLine.CsvNextItemToDouble();  
            double d2 = sLine.CsvNextItemToDouble(); 
            
            mstring val = mstring.Prepare();
            val = val.AppendM(label).AppendM(d).AppendM(',').AppendM(d2);
            
            output.Add(val);               
        }                        
  }

Job.Local:

public virtual void Local()
{
    //Compare input and output file, pass if they are the same.
    Shell(@"Qizmt exec regression_test_checkTestResult.xml regression_test_mstring.CsvNextItemToDouble_Input.txt regression_test_mstring.CsvNextItemToDouble_Output.txt regression_test_mstring.CsvNextItemToDouble.xml double");          
    
    Shell(@"Qizmt del regression_test_mstring.CsvNextItemToDouble_Input.txt", true);
    Shell(@"Qizmt del regression_test_mstring.CsvNextItemToDouble_Output.txt", true); 
}
