Job.Local:

public virtual void Local()
{
    Shell(@"Qizmt del regression_test_mstring.Prepare(char)_Input.txt", true);
    Shell(@"Qizmt del regression_test_mstring.Prepare(char)_Output.txt", true);    
}

Job.Remote:

  public virtual void Remote(RemoteInputStream dfsinput, RemoteOutputStream dfsoutput)
  {             
        //Create sample data.
        int stringLength = 100;
        int rowCount = 2000;                
                       
        Random rnd = new Random(System.DateTime.Now.Millisecond / 2 + System.Diagnostics.Process.GetCurrentProcess().Id / 2);
    
        List<byte> onerow = new List<byte>();
        
        for(long rn = 0; rn < rowCount; rn++)
        {
            onerow.Clear();
            
            //string  
            for(int cnt = 0; cnt < stringLength; cnt++)
            {
                byte b = (byte)rnd.Next((int)' ' + 1, (int)'~' + 1);   
                
                onerow.Add(b); 
            }    
            
            dfsoutput.WriteLine(onerow);        
        }     
   }

MapReduce.Map:
  
  public virtual void Map(ByteSlice line, MapOutput output)
  {
        string sLine = line.ToString();
        
        mstring val = mstring.Prepare();
        
        for(int i = 0; i < sLine.Length; i++)
        {
            char c = sLine[i];
            mstring mc = mstring.Prepare(c);
            val = val.AppendM(mc);
        }
        
        output.Add(mstring.Prepare(sLine[0]), val);               
  }

MapReduce.Reduce:
  
  public override void Reduce(ByteSlice key, ByteSliceList values, ReduceOutput output)
  {                          
        for(int i = 0; i < values.Length; i++)
        {                   
            string sLine = values.Items[i].ToString();
        
            mstring val = mstring.Prepare();
            
            for(int k = 0; k < sLine.Length; k++)
            {
                char c = sLine[k];
                mstring mc = mstring.Prepare(c);
                val = val.AppendM(mc);
            }
            
            output.Add(val);               
        }                        
  }

Job.Local:

public virtual void Local()
{
    //Compare input and output file, pass if they are the same.
    Shell(@"Qizmt exec regression_test_checkTestResult.xml regression_test_mstring.Prepare(char)_Input.txt regression_test_mstring.Prepare(char)_Output.txt regression_test_mstring.Prepare(char).xml");          
    
    Shell(@"Qizmt del regression_test_mstring.Prepare(char)_Input.txt", true);
    Shell(@"Qizmt del regression_test_mstring.Prepare(char)_Output.txt", true); 
}
