Job.Remote:

  public virtual void Remote(RemoteInputStream dfsinput, RemoteOutputStream dfsoutput)
  {             
      List<byte> buf = new List<byte>();
      while(dfsinput.ReadLineAppend(buf))
      {
          dfsoutput.WriteRecord(buf);
          buf.Clear();
      }
   }


