MapReduce.Map:
  
  public virtual void Map(ByteSlice line, MapOutput output)
  {
        int key = 0;
        if(StaticGlobals.DSpace_InputFileName == "regression_test_DSpace_InputFileName_Map_Cache_Table_InputTBL1.txt")
        {
            key = 1;
        }
        else if(StaticGlobals.DSpace_InputFileName == "regression_test_DSpace_InputFileName_Map_Cache_Table_InputTBL2.txt")
        {
            key = 2;
        }
        else if(StaticGlobals.DSpace_InputFileName == "regression_test_DSpace_InputFileName_Map_Cache_Table_InputTBL3.txt")
        {
            key = 3;
        }
        else if(StaticGlobals.DSpace_InputFileName == "regression_test_DSpace_InputFileName_Map_Cache_Table_InputTBL4.txt")
        {
            key = 4;
        }
        recordset rkey = recordset.Prepare();
        rkey.PutInt(key);
        recordset rval = recordset.Prepare();
        rval.PutBytes(line.ToBytes(), 0, 100);               
        output.Add(rkey, rval);
  }

