Job.Local:
        
        public virtual void Local()
        {
            string dir = IOUtils.GetTempDirectory();          
            string result1 = dir + @"\" + Guid.NewGuid().ToString();
            string result2 = dir + @"\" + Guid.NewGuid().ToString();
            string result3 = dir + @"\" + Guid.NewGuid().ToString();     
            string result4 = dir + @"\" + Guid.NewGuid().ToString();
            string expected1 = dir + @"\" + Guid.NewGuid().ToString();
            string expected2 = dir + @"\" + Guid.NewGuid().ToString();
            string expected3 = dir + @"\" + Guid.NewGuid().ToString();
            string expected4 = dir + @"\" + Guid.NewGuid().ToString();
            
            Shell(@"Qizmt get regression_test_DSpace_InputFileName_Map_Cache_Table_Input1_Sorted.txt " + expected1);
            Shell(@"Qizmt get regression_test_DSpace_InputFileName_Map_Cache_Table_Input2_Sorted.txt " + expected2);
            Shell(@"Qizmt get regression_test_DSpace_InputFileName_Map_Cache_Table_Input3_Sorted.txt " + expected3);
            Shell(@"Qizmt get regression_test_DSpace_InputFileName_Map_Cache_Table_Input4_Sorted.txt " + expected4);
            Shell(@"Qizmt get regression_test_DSpace_InputFileName_Map_Cache_Table_Output1_Sorted.txt " + result1);
            Shell(@"Qizmt get regression_test_DSpace_InputFileName_Map_Cache_Table_Output2_Sorted.txt " + result2);
            Shell(@"Qizmt get regression_test_DSpace_InputFileName_Map_Cache_Table_Output3_Sorted.txt " + result3);
            Shell(@"Qizmt get regression_test_DSpace_InputFileName_Map_Cache_Table_Output4_Sorted.txt " + result4);
            
            bool ok = false;                        
            ok = CompareFiles(result1, expected1);   
            
            if(ok)
            {
                Qizmt_Log("File 1 is ok");
                ok = CompareFiles(result2, expected2);  
                if(ok)
                {
                    Qizmt_Log("File 2 is ok");
                    ok = CompareFiles(result3, expected3);  
                    if(ok)
                    {
                        Qizmt_Log("File 3 is ok");
                        ok = CompareFiles(result4, expected4);  
                        if(ok)
                        {
                            Qizmt_Log("File 4 is ok");
                        }
                    }
                }
            }        
                 
            if(ok)
            {
                Shell(@"Qizmt exec regression_test_writePASSFAIL.xml regression_test_DSpace_InputFileName_Map_Cache_Table.xml 1");
            }
            else
            {
                Shell(@"Qizmt exec regression_test_writePASSFAIL.xml regression_test_DSpace_InputFileName_Map_Cache_Table.xml 0");
            }
            
            Shell(@"Qizmt del regression_test_DSpace_InputFileName_Map_Cache_Table_Input*.txt");
            Shell(@"Qizmt del regression_test_DSpace_InputFileName_Map_Cache_Table_xInput*.txt");
            Shell(@"Qizmt del regression_test_DSpace_InputFileName_Map_Cache_Table_Output*.txt");            
            Shell(@"Qizmt del regression_test_DSpace_InputFileName_Map_Cache_Table_theCache");
            System.IO.File.Delete(result1);
            System.IO.File.Delete(result2);
            System.IO.File.Delete(result3);
            System.IO.File.Delete(result4);
            System.IO.File.Delete(expected1);
            System.IO.File.Delete(expected2);
            System.IO.File.Delete(expected3);
            System.IO.File.Delete(expected4);
        }
                
        private static bool CompareFiles(string f1, string f2)
        {
            System.IO.FileInfo info1 = new System.IO.FileInfo(f1);
            System.IO.FileInfo info2 = new System.IO.FileInfo(f2);
            if (info1.Length != info2.Length)
            {
                return false;
            }

            System.IO.FileStream fs1 = new System.IO.FileStream(f1, System.IO.FileMode.Open);
            System.IO.FileStream fs2 = new System.IO.FileStream(f2, System.IO.FileMode.Open);
            bool ok = true;
            for (int i = 0; i < info1.Length; i++)
            {
                int b1 = fs1.ReadByte();
                int b2 = fs2.ReadByte();
                if (b1 != b2)
                {
                    ok = false;
                    break;
                }
            }

            fs1.Close();
            fs2.Close();
            return ok;
        }
