Job.Local:

public virtual void Local()
{
    Shell(@"Qizmt del regression_test_Entry.BytesToDouble(List_int)_Input.txt", true);
    Shell(@"Qizmt del regression_test_Entry.BytesToDouble(List_int)_Output.txt", true);
}

Job.Remote:

  public virtual void Remote(RemoteInputStream dfsinput, RemoteOutputStream dfsoutput)
  {             
        //Create sample data.
        int rowCount = 2000;
        char del = ',';
                       
        Random rnd = new Random(System.DateTime.Now.Millisecond / 2 + System.Diagnostics.Process.GetCurrentProcess().Id / 2);
    
        List<byte> onerow = new List<byte>();
        
        for(long rn = 0; rn < rowCount; rn++)
        {
            onerow.Clear();
            
            //label
            string s = rn.ToString();
            byte[] buf = System.Text.Encoding.UTF8.GetBytes(s);
            
            foreach(byte b in buf)
            {
                onerow.Add(b);
            }
            
            onerow.Add((byte)':');
            
            //double  
            double d = rnd.NextDouble();
            
            if(rnd.Next() % 2 == 1)
            {
                d = d * -1;
            }
            
            s = d.ToString();
            buf = System.Text.Encoding.UTF8.GetBytes(s);
            
            foreach(byte b in buf)
            {
                onerow.Add(b);
            }  
            
            onerow.Add((byte)del);
            
            foreach(byte b in buf)
            {
                onerow.Add(b);
            }  
            
            dfsoutput.WriteLine(onerow);        
        }     
   }

MapReduce.Map:
  
  public virtual void Map(ByteSlice line, MapOutput output)
  {
       string s = line.ToString();
       int col = s.IndexOf(':');
       string label = s.Substring(0, col+1);
       
       int del = s.IndexOf(',');
       
       double d = double.Parse(s.Substring(col + 1, del - col - 1));
       byte[] buf = new byte[9];
       Entry.DoubleToBytes(d, buf, 0); 
       d = Entry.BytesToDouble(buf, 0);
       Entry.DoubleToBytes(d, buf, 0); 
       
       ByteSlice key = ByteSlice.PreparePaddedStringAscii(label, 16);
       ByteSlice val = ByteSlice.Prepare(buf);
       output.Add(key, val);
  }

MapReduce.Reduce:
  
  public override void Reduce(ByteSlice key, ByteSliceList values, ReduceOutput output)
  {                  
        key = UnpadKey(key);
        string label = key.ToString();
        
        for(int i = 0; i < values.Length; i++)
        {
            double d = Entry.BytesToDouble(values.Items[i].ToBytes(), 0);
            string s = label + d.ToString() + "," + d.ToString();
            ByteSlice val = ByteSlice.Prepare(s);
            output.Add(val);
        }                        
  }

Job.Local:

public virtual void Local()
{
    //Compare input and output file, pass if they are the same.
    Shell(@"Qizmt exec regression_test_checkTestResult.xml regression_test_Entry.BytesToDouble(List_int)_Input.txt regression_test_Entry.BytesToDouble(List_int)_Output.txt regression_test_Entry.BytesToDouble(List_int).xml double");          
    
    Shell(@"Qizmt del regression_test_Entry.BytesToDouble(List_int)_Input.txt", true);
    Shell(@"Qizmt del regression_test_Entry.BytesToDouble(List_int)_Output.txt", true); 
}
