Job.Local:

public virtual void Local()
{
    Shell(@"Qizmt del regression_test_KeyLength_Int16_Input.txt", true);
    Shell(@"Qizmt del regression_test_KeyLength_Int16_Output.txt", true);
}

Job.Remote:

  public virtual void Remote(RemoteInputStream dfsinput, RemoteOutputStream dfsoutput)
  {             
        //Create sample data.
        int rowCount = 2000;
        char del = ','; 
                       
        Random rnd = new Random(System.DateTime.Now.Millisecond / 2 + System.Diagnostics.Process.GetCurrentProcess().Id / 2);
    
        List<byte> onerow = new List<byte>();
        
        for(long rn = 0; rn < rowCount; rn++)
        {
            onerow.Clear();
            
            Int16 x =(Int16)rnd.Next();
            
            string s = x.ToString();
            byte[] buf = System.Text.Encoding.UTF8.GetBytes(s);
            
            foreach(byte b in buf)
            {
                onerow.Add(b);
            }                    
           
            dfsoutput.WriteLine(onerow);        
        } 
   }

MapReduce.Map:
  
  public virtual void Map(ByteSlice line, MapOutput output)
  {
       string s = line.ToString();
       Int16 x = Int16.Parse(s);
       
       recordset rKey = recordset.Prepare();
       recordset rValue = recordset.Prepare();
       
       rKey.PutInt16(x);
       rValue.PutInt16(x);
       
       output.Add(rKey, rValue);
  }

MapReduce.Reduce:
  
  public override void Reduce(ByteSlice key, ByteSliceList values, ReduceOutput output)
  {               
        for(int i = 0; i < values.Length; i++)
        {
             recordset rValue = recordset.Prepare(values.Items[i]);
             Int16 val = rValue.GetInt16();
             string sVal = val.ToString();
            
             output.Add(mstring.Prepare(sVal)); 
        }                        
  }

