   }

MapReduce.Map:
  
  public virtual void Map(ByteSlice line, MapOutput output)
  {
        Blob blob = line.ReadBinary();
        string imgName = blob.name;
        byte[] data = blob.data;
        
        mstring key = mstring.Prepare(imgName);
        recordset value = recordset.Prepare();
        value.PutBytes(data, 0, data.Length);
        
        output.Add(key, value);
  }

MapReduce.Reduce:
          
          public override void Reduce(ByteSlice key, ByteSliceList values, ReduceOutput output)
          {
              string imgName = mstring.Prepare(UnpadKey(key)).ToString();
              byte[] data = values[0].Value.ToBytes();
              
              System.IO.MemoryStream ms = new System.IO.MemoryStream(data);
              System.Drawing.Bitmap srcBmp = new System.Drawing.Bitmap(ms);
              ms.Close();
              
              byte[] canny = Canny(srcBmp);
              srcBmp = null;
              
              Blob blob = Blob.Prepare("canny.bmp", canny);
              output.AddBinary(blob);
          }
          
          private byte[] Canny(System.Drawing.Bitmap srcBmp)
        {
            openCV.IplImage image = openCV.cvlib.ToIplImage(srcBmp, false);

            //create gray scale image
            openCV.IplImage gray = openCV.cvlib.CvCreateImage(new openCV.CvSize(image.width, image.height), (int)openCV.cvlib.IPL_DEPTH_8U, 1);

            //color conversion
            openCV.cvlib.CvCvtColor(ref image, ref gray, openCV.cvlib.CV_BGR2GRAY);

            //edge image
            openCV.IplImage edges = openCV.cvlib.CvCloneImage(ref gray);

            //canny filter
            openCV.cvlib.CvCanny(ref gray, ref edges, 255, 255, 3);

            //release temp image
            openCV.cvlib.CvReleaseImage(ref gray);

            //crate color image back
            openCV.cvlib.CvCvtColor(ref edges, ref image, openCV.cvlib.CV_GRAY2BGR);

            //release temp image
            openCV.cvlib.CvReleaseImage(ref edges);

            //Save the image
            System.Drawing.Bitmap bmp = openCV.cvlib.ToBitmap(image, false);
            System.IO.MemoryStream ms = new System.IO.MemoryStream();
            bmp.Save(ms, System.Drawing.Imaging.ImageFormat.Bmp);
            byte[] buf = ms.ToArray();

            ms.Close();
            bmp = null;

            if (edges.imageData != IntPtr.Zero)
            {
                openCV.cvlib.CvReleaseImage(ref edges);
            }

            if (gray.imageData != IntPtr.Zero)
            {
                openCV.cvlib.CvReleaseImage(ref gray);
            }

            if (image.imageData != IntPtr.Zero)
            {
                openCV.cvlib.CvReleaseImage(ref image);
            }

            return buf;
        }

