Job.Local:

public virtual void Local()
{
    Shell(@"Qizmt del regression_test_mapreduce_fail_largekeylength_Input.txt");
    Shell(@"Qizmt del regression_test_mapreduce_fail_largekeylength_Output.txt");
}

Job.Remote:

    public virtual void Remote(RemoteInputStream dfsinput, RemoteOutputStream dfsoutput)
    {
        dfsoutput.WriteLine("testing");
    }

MapReduce.Map:
  
  public virtual void Map(ByteSlice line, MapOutput output)
  {
      try
      {
        output.Add(ByteSlice.Prepare(line, 0, 5), ByteSlice.Prepare());
      }
      catch
      {
        output.Add(ByteSlice.Prepare(line, 0, 4), line);
      }
  }

MapReduce.Reduce:
  
  public override void Reduce(ByteSlice key, ByteSliceList values, ReduceOutput output)
  {
      IEnumerator<ByteSlice> evalues = values;
      while(evalues.MoveNext())
      {
          output.Add(evalues.Current);
      }
  }

Job.Local:

public virtual void Local()
{
    Shell(@"Qizmt exec regression_test_iocompare.xml regression_test_mapreduce_fail_largekeylength_Input.txt regression_test_mapreduce_fail_largekeylength_Output.txt regression_test_mapreduce_fail_largekeylength.xml");          
    
    Shell(@"Qizmt del regression_test_mapreduce_fail_largekeylength_Input.txt");
    Shell(@"Qizmt del regression_test_mapreduce_fail_largekeylength_Output.txt");
}
