Job.Local:

public virtual void Local()
{
    Shell(@"Qizmt del regression_test_mstring.Contains(mstring)_Input.txt", true);
    Shell(@"Qizmt del regression_test_mstring.Contains(mstring)_Output.txt", true); 
    Shell(@"Qizmt del regression_test_mstring.Contains(mstring)_Output1.txt", true); 
    Shell(@"Qizmt del regression_test_mstring.Contains(mstring)_Output2.txt", true); 
}

Job.Remote:

  public virtual void Remote(RemoteInputStream dfsinput, RemoteOutputStream dfsoutput)
  {             
        //Bug in BTree, first line of input file must produce a key/value pair in mapper, if not, exception occurs.     
        string t = "";
        for(int i=0; i < 20; i++)
        {
            t += "0123456789";
        }
        dfsoutput.WriteLine(t); 
        
        //Create sample data.
        int stringLength = 200;
        int rowCount = 2000;    
                                      
        Random rnd = new Random(System.DateTime.Now.Millisecond / 2 + System.Diagnostics.Process.GetCurrentProcess().Id / 2);
    
        List<byte> onerow = new List<byte>();
        
        for(long rn = 0; rn < rowCount; rn++)
        {
            onerow.Clear();
            
            //string  
            for(int cnt = 0; cnt < stringLength; cnt++)
            {
                byte b = (byte)rnd.Next((int)' ' + 1, (int)'~' + 1); 
                onerow.Add(b);  
            }    
            
            dfsoutput.WriteLine(onerow);        
        }
   }

MapReduce.Map:
  
  public virtual void Map(ByteSlice line, MapOutput output)
  {
        string sLine = line.ToString();
        mstring s = mstring.Prepare(sLine.Substring(2));
        mstring c = mstring.Prepare(sLine.Substring(0, 2));              
    
        if(s.Contains(c))
        {
            ByteSlice key = ByteSlice.Prepare(sLine.Substring(0, 1));
            output.Add(key, line);                    
        }           
  }

MapReduce.Reduce:
  
  public override void Reduce(ByteSlice key, ByteSliceList values, ReduceOutput output)
  {                          
        for(int i = 0; i < values.Length; i++)
        {
            string sLine = values.Items[i].ToString();
            mstring s = mstring.Prepare(sLine.Substring(4));
            mstring c = mstring.Prepare(sLine.Substring(2, 2));             
        
            if(s.Contains(c))
            {
                output.Add(values.Items[i]);                    
            }              
        }                        
  }

Job.Remote:

  public virtual void Remote(RemoteInputStream dfsinput, RemoteOutputStream dfsoutput)
  {             
        StringBuilder sb = new StringBuilder();
        
        while(dfsinput.ReadLineAppend(sb))
        {
            string sLine = sb.ToString();
            string s = sLine.Substring(2);
            string c = sLine.Substring(0, 2);  
           
            if(s.Contains(c))
            {      
                dfsoutput.WriteLine(sLine);                    
            }  
            
            sb.Length = 0;
        }              
   }


