Job.Local:

public virtual void Local()
{
    //Compare input and output file, pass if they are the same.
    Shell(@"Qizmt exec regression_test_checkTestResult.xml regression_test_mstring.Contains(mstring)_Output.txt regression_test_mstring.Contains(mstring)_Output2.txt regression_test_mstring.Contains(mstring).xml");          
    
    Shell(@"Qizmt del regression_test_mstring.Contains(mstring)_Input.txt", true);
    Shell(@"Qizmt del regression_test_mstring.Contains(mstring)_Output.txt", true); 
