Job.Local:

public virtual void Local()
{
    Shell(@"Qizmt del regression_test_mstring.IndexOf(mstring)_Input.txt", true);
    Shell(@"Qizmt del regression_test_mstring.IndexOf(mstring)_Output.txt", true);   
}

Job.Remote:

  public virtual void Remote(RemoteInputStream dfsinput, RemoteOutputStream dfsoutput)
  {             
        //Create sample data.
        int stringLength = 100;
        int rowCount = 2000;
                       
        Random rnd = new Random(System.DateTime.Now.Millisecond / 2 + System.Diagnostics.Process.GetCurrentProcess().Id / 2);
    
        List<byte> onerow = new List<byte>();
        
        for(long rn = 0; rn < rowCount; rn++)
        {
            onerow.Clear();
            
            //string  
            for(int cnt = 0; cnt < stringLength; cnt++)
            {
                byte b = (byte)rnd.Next((int)' ' + 1, (int)'~' + 1);   
                
                onerow.Add(b); 
            }     
            
            dfsoutput.WriteLine(onerow);        
        }     
   }

MapReduce.Map:
  
  public virtual void Map(ByteSlice line, MapOutput output)
  {
        string sLine = line.ToString();  
        string s1 = sLine.Substring(0, 25);
        string s2 = sLine.Substring(25);
        
        mstring ms = mstring.Prepare(sLine);
        mstring m1 = mstring.Prepare(s1);
        mstring m2 = mstring.Prepare(s2);
        
        int i1 = ms.IndexOf(m1);    //0
        int i2 = ms.IndexOf(m2);    //25
        
        ByteSlice key = ByteSlice.Prepare(sLine.Substring(0, 1));
        ByteSlice val = ByteSlice.Prepare(sLine.Substring(i1, 25) + sLine.Substring(i2));
       
        output.Add(key, val);               
  }

MapReduce.Reduce:
  
  public override void Reduce(ByteSlice key, ByteSliceList values, ReduceOutput output)
  {                          
        for(int i = 0; i < values.Length; i++)
        {
            string sLine = values.Items[i].ToString();  
            string s1 = sLine.Substring(0, 25);
            string s2 = sLine.Substring(25);
            
            mstring ms = mstring.Prepare(sLine);
            mstring m1 = mstring.Prepare(s1);
            mstring m2 = mstring.Prepare(s2);
            
            int i1 = ms.IndexOf(m1);    //0
            int i2 = ms.IndexOf(m2);    //25                    
          
            ByteSlice val = ByteSlice.Prepare(sLine.Substring(i1, 25) + sLine.Substring(i2));
           
            output.Add(val);                      
        }                        
  }

Job.Local:

public virtual void Local()
{
    //Compare input and output file, pass if they are the same.
    Shell(@"Qizmt exec regression_test_checkTestResult.xml regression_test_mstring.IndexOf(mstring)_Input.txt regression_test_mstring.IndexOf(mstring)_Output.txt regression_test_mstring.IndexOf(mstring).xml");          
    
    Shell(@"Qizmt del regression_test_mstring.IndexOf(mstring)_Input.txt", true);
    Shell(@"Qizmt del regression_test_mstring.IndexOf(mstring)_Output.txt", true);
}
