Job.Local:

public virtual void Local()
{
    Shell(@"Qizmt del regression_test_mstring.MAppend(UInt64)_Input.txt", true);
    Shell(@"Qizmt del regression_test_mstring.MAppend(UInt64)_Output.txt", true);    
}

Job.Remote:

  public virtual void Remote(RemoteInputStream dfsinput, RemoteOutputStream dfsoutput)
  {             
        //Create sample data.
        int rowCount = 2000;
        char del = ',';
                       
        Random rnd = new Random(System.DateTime.Now.Millisecond / 2 + System.Diagnostics.Process.GetCurrentProcess().Id / 2);
        List<byte> onerow = new List<byte>();
        
        {
            UInt64 l = UInt64.MaxValue;
            string s = l.ToString();
            byte[] buf = System.Text.Encoding.UTF8.GetBytes(s);
            
            foreach(byte b in buf)
            {
                onerow.Add(b);
            }
            
            onerow.Add((byte)del);
            
            l = UInt64.MinValue;
            s = l.ToString();
            buf = System.Text.Encoding.UTF8.GetBytes(s);
            
            foreach(byte b in buf)
            {
                onerow.Add(b);
            }
            dfsoutput.WriteLine(onerow);     
        }
        
        for(long rn = 0; rn < rowCount; rn++)
        {
            onerow.Clear();
            
            //UInt64 
            UInt64 l = (UInt64)DateTime.Now.Ticks + (UInt64)rnd.Next();
            
            string s = l.ToString();
            byte[] buf = System.Text.Encoding.UTF8.GetBytes(s);
            
            foreach(byte b in buf)
            {
                onerow.Add(b);
            }
            
            onerow.Add((byte)del);
            
            //long  
            l = (UInt64)DateTime.Now.Ticks + (UInt64)rnd.Next();
            
            s = l.ToString();
            buf = System.Text.Encoding.UTF8.GetBytes(s);
            
            foreach(byte b in buf)
            {
                onerow.Add(b);
            }
            
            dfsoutput.WriteLine(onerow);        
        }     
   }

MapReduce.Map:
  
  public virtual void Map(ByteSlice line, MapOutput output)
  {
        string sLine = line.ToString(); 
        int del = sLine.IndexOf(',');
        
        UInt64 l = UInt64.Parse(sLine.Substring(0, del));  
        UInt64 l2 = UInt64.Parse(sLine.Substring(del + 1));
       
        int k = (int)(l % 10);
        string key = k.ToString();
        
        mstring val = mstring.Prepare();
        val = val.MAppend(l).MAppend(',').MAppend(l2);
        
        output.Add(mstring.Prepare(key), val);               
  }

MapReduce.Reduce:
  
  public override void Reduce(ByteSlice key, ByteSliceList values, ReduceOutput output)
  {                          
        for(int i = 0; i < values.Length; i++)
        {
            string sLine = values.Items[i].ToString(); 
            int del = sLine.IndexOf(',');
            
            UInt64 l = UInt64.Parse(sLine.Substring(0, del));  
            UInt64 l2 = UInt64.Parse(sLine.Substring(del + 1));
            
            mstring val = mstring.Prepare();
            val = val.MAppend(l).MAppend(',').MAppend(l2);
            
            output.Add(val);                       
        }                        
  }

Job.Local:

public virtual void Local()
{
    //Compare input and output file, pass if they are the same.
    Shell(@"Qizmt exec regression_test_checkTestResult.xml regression_test_mstring.MAppend(UInt64)_Input.txt regression_test_mstring.MAppend(UInt64)_Output.txt regression_test_mstring.MAppend(UInt64).xml");          
    
    Shell(@"Qizmt del regression_test_mstring.MAppend(UInt64)_Input.txt", true);
    Shell(@"Qizmt del regression_test_mstring.MAppend(UInt64)_Output.txt", true); 
}
