Job.Local:

public virtual void Local()
{
    Shell(@"Qizmt del regression_test_mstring.MToUpper_Reducer_Input.txt", true);
    Shell(@"Qizmt del regression_test_mstring.MToUpper_Reducer_Output.txt", true);  
    Shell(@"Qizmt del regression_test_mstring.MToUpper_Reducer_Output1.txt", true); 
}

Job.Remote:

  public virtual void Remote(RemoteInputStream dfsinput, RemoteOutputStream dfsoutput)
  {             
        //Create sample data.
        int stringLength = 300;
        int rowCount = 2000;                
                       
        Random rnd = new Random(System.DateTime.Now.Millisecond / 2 + System.Diagnostics.Process.GetCurrentProcess().Id / 2);
    
        List<byte> onerow = new List<byte>();
        
        for(long rn = 0; rn < rowCount; rn++)
        {
            onerow.Clear();
            
            //string  
            for(int cnt = 0; cnt < stringLength; cnt++)
            {
                byte b = (byte)rnd.Next((int)' ' + 1, (int)'~' + 1);   
                
               onerow.Add(b);   
            }     
            
            dfsoutput.WriteLine(onerow);        
        }     
   }

MapReduce.Map:
  
  public virtual void Map(ByteSlice line, MapOutput output)
  {
      string t = line.ToString();   
      output.Add(ByteSlice.Prepare(t.Substring(0, 1)), line);               
  }

MapReduce.Reduce:
  
  public override void Reduce(ByteSlice key, ByteSliceList values, ReduceOutput output)
  {                      
        for(int i = 0; i < values.Length; i++)
        {   
              mstring val = mstring.Prepare(values.Items[i]);
              val = val.MToUpper();
              output.Add(val);                
        }                        
  }

Job.Remote:

  public virtual void Remote(RemoteInputStream dfsinput, RemoteOutputStream dfsoutput)
  {             
        StringBuilder sb = new StringBuilder();
        
        while(dfsinput.ReadLineAppend(sb))
        {
            string sLine = sb.ToString();
            dfsoutput.WriteLine(sLine.ToUpper());
            sb.Length = 0;
        }
   }

Job.Local:

public virtual void Local()
{
    //Compare input and output file, pass if they are the same.
    Shell(@"Qizmt exec regression_test_checkTestResult.xml regression_test_mstring.MToUpper_Reducer_Output.txt regression_test_mstring.MToUpper_Reducer_Output1.txt regression_test_mstring.MToUpper_Reducer.xml");          
    
    Shell(@"Qizmt del regression_test_mstring.MToUpper_Reducer_Input.txt", true);
    Shell(@"Qizmt del regression_test_mstring.MToUpper_Reducer_Output.txt", true);
    Shell(@"Qizmt del regression_test_mstring.MToUpper_Reducer_Output1.txt", true); 
}
