Job.Local:

public virtual void Local()
{
    Shell(@"Qizmt del regression_test_mstring.NextItemToLong_Input.txt", true);
    Shell(@"Qizmt del regression_test_mstring.NextItemToLong_Output.txt", true);    
}

Job.Remote:

  public virtual void Remote(RemoteInputStream dfsinput, RemoteOutputStream dfsoutput)
  {             
        //Create sample data.
        int rowCount = 2000;
        char del = ',';
                       
        Random rnd = new Random(System.DateTime.Now.Millisecond / 2 + System.Diagnostics.Process.GetCurrentProcess().Id / 2);
        List<byte> onerow = new List<byte>();
        
        for(long rn = 0; rn < rowCount; rn++)
        {
            onerow.Clear();
            
            //long 
            long l = DateTime.Now.Ticks + (long)rnd.Next();
            
            if(rnd.Next() % 2 == 1)
            {
                l = l * (-1);
            }
            
            string s = l.ToString();
            byte[] buf = System.Text.Encoding.UTF8.GetBytes(s);
            
            foreach(byte b in buf)
            {
                onerow.Add(b);
            }
            
            onerow.Add((byte)del);
            
            //long  
            l = DateTime.Now.Ticks + (long)rnd.Next();
            
            if(rnd.Next() % 2 == 1)
            {
                l = l * (-1);
            }
            
            s = l.ToString();
            buf = System.Text.Encoding.UTF8.GetBytes(s);
            
            foreach(byte b in buf)
            {
                onerow.Add(b);
            }
            
            dfsoutput.WriteLine(onerow);        
        } 
   }

MapReduce.Map:
  
  public virtual void Map(ByteSlice line, MapOutput output)
  {
        mstring sLine = mstring.Prepare(line);
        
        long l = sLine.NextItemToLong(',');
        long l2 = sLine.NextItemToLong(',');
       
        int k = (int)(l % 10);
        string key = k.ToString();
        
        mstring val = mstring.Prepare();
        val = val.AppendM(l).AppendM(',').AppendM(l2);
        
        output.Add(mstring.Prepare(key), val);              
  }

MapReduce.Reduce:
  
  public override void Reduce(ByteSlice key, ByteSliceList values, ReduceOutput output)
  {                          
        for(int i = 0; i < values.Length; i++)
        {
            mstring sLine = mstring.Prepare(values.Items[i]);
        
            long l = sLine.NextItemToLong(',');
            long l2 = sLine.NextItemToLong(',');
            
            mstring val = mstring.Prepare();
            val = val.AppendM(l).AppendM(',').AppendM(l2);
            
            output.Add(val);             
        }                        
  }

Job.Local:

public virtual void Local()
{
    //Compare input and output file, pass if they are the same.
    Shell(@"Qizmt exec regression_test_checkTestResult.xml regression_test_mstring.NextItemToLong_Input.txt regression_test_mstring.NextItemToLong_Output.txt regression_test_mstring.NextItemToLong.xml");          
    
    Shell(@"Qizmt del regression_test_mstring.NextItemToLong_Input.txt", true);
    Shell(@"Qizmt del regression_test_mstring.NextItemToLong_Output.txt", true); 
}
