Job.Local:

public virtual void Local()
{
    Shell(@"Qizmt del regression_test_mstring.ReplaceM(char_char)_Input.txt", true);
    Shell(@"Qizmt del regression_test_mstring.ReplaceM(char_char)_Output.txt", true);   
    Shell(@"Qizmt del regression_test_mstring.ReplaceM(char_char)_Output1.txt", true);    
    Shell(@"Qizmt del regression_test_mstring.ReplaceM(char_char)_Output2.txt", true);    
}

Job.Remote:

  public virtual void Remote(RemoteInputStream dfsinput, RemoteOutputStream dfsoutput)
  {             
        //Create sample data.
        int stringLength = 100;
        int rowCount = 2000;               
                       
        Random rnd = new Random(System.DateTime.Now.Millisecond / 2 + System.Diagnostics.Process.GetCurrentProcess().Id / 2);
    
        List<byte> onerow = new List<byte>();
        
        for(long rn = 0; rn < rowCount; rn++)
        {
            onerow.Clear();
            
            //string  
            for(int cnt = 0; cnt < stringLength; cnt++)
            {
                byte b = (byte)rnd.Next((int)' ' + 1, (int)'~' + 1);   
                
               onerow.Add(b);   
            }
            
            dfsoutput.WriteLine(onerow);        
        }     
   }

MapReduce.Map:
  
  public virtual void Map(ByteSlice line, MapOutput output)
  {
        string sLine = line.ToString();                 
        char oldChar = sLine[0];
        char newChar = sLine[1];
        mstring ms = mstring.Prepare(line);                
        mstring val = ms.ReplaceM(oldChar, newChar);
        
        output.Add(mstring.Prepare(oldChar), val);               
  }

MapReduce.Reduce:
  
  public override void Reduce(ByteSlice key, ByteSliceList values, ReduceOutput output)
  {                          
        for(int i = 0; i < values.Length; i++)
        {
            string sLine = values.Items[i].ToString();                 
            char oldChar = sLine[1];
            char newChar = sLine[2];
            mstring ms = mstring.Prepare(values.Items[i]);                    
            mstring val = ms.ReplaceM(oldChar, newChar);
            
            output.Add(val);                    
        }                        
  }

Job.Remote:

  public virtual void Remote(RemoteInputStream dfsinput, RemoteOutputStream dfsoutput)
  {             
        StringBuilder sb = new StringBuilder();
        
        while(dfsinput.ReadLineAppend(sb))
        {
            string sLine = sb.ToString();               
            char oldChar = sLine[0];
            char newChar = sLine[1];
            string val = sLine.Replace(oldChar, newChar);
            dfsoutput.WriteLine(val);                    
            
            sb.Length = 0;                    
        }
   }


  public virtual void Remote(RemoteInputStream dfsinput, RemoteOutputStream dfsoutput)
  {             
        StringBuilder sb = new StringBuilder();
        
        while(dfsinput.ReadLineAppend(sb))
        {
            string sLine = sb.ToString();               
            char oldChar = sLine[1];
            char newChar = sLine[2];
            string val = sLine.Replace(oldChar, newChar);
            dfsoutput.WriteLine(val);                    
            
            sb.Length = 0;                    
        }
   }

Job.Local:

public virtual void Local()
{
    //Compare input and output file, pass if they are the same.
    Shell(@"Qizmt exec regression_test_checkTestResult.xml regression_test_mstring.ReplaceM(char_char)_Output.txt regression_test_mstring.ReplaceM(char_char)_Output2.txt regression_test_mstring.ReplaceM(char_char).xml");          
    
    Shell(@"Qizmt del regression_test_mstring.ReplaceM(char_char)_Input.txt", true);
    Shell(@"Qizmt del regression_test_mstring.ReplaceM(char_char)_Output.txt", true); 
    Shell(@"Qizmt del regression_test_mstring.ReplaceM(char_char)_Output1.txt", true); 
    Shell(@"Qizmt del regression_test_mstring.ReplaceM(char_char)_Output2.txt", true); 
}
