}

MapReduce.Map:
  
  public virtual void Map(ByteSlice line, MapOutput output)
  {
      output.Add(line, ByteSlice.Prepare());
  }

MapReduce.Reduce:
  
  public override void Reduce(ByteSlice key, ByteSliceList values, ReduceOutput output)
  { 
      for(int i = 0; i < values.Length; i++)
      {                  
          output.Add(key);
      }
  }

Job.Remote:

  public virtual void Remote(RemoteInputStream dfsinput, RemoteOutputStream dfsoutput)
  {             
        StringBuilder sb = new StringBuilder();
        List<string> lines = new List<string>();
        while(dfsinput.ReadLineAppend(sb))
        {
            lines.Add(sb.ToString());
            sb.Length = 0;
        }
        
        for(int i = lines.Count - 1; i >= 0; i--)
        {
            dfsoutput.WriteLine(lines[i]);
        }
   }

Job.Local:

public virtual void Local()
{
    string f1 = IOUtils.GetTempDirectory() + @"\" + Guid.NewGuid().ToString();
    string f2 = IOUtils.GetTempDirectory() + @"\" + Guid.NewGuid().ToString();
    
    Shell(@"Qizmt get regression_test_outputDirection_desc_explicitCache_OutputD.txt " + f1);
    Shell(@"Qizmt get regression_test_outputDirection_desc_explicitCache_OutputD2.txt " + f2);
    
    if(CompareFiles(f1, f2))
    {
        Shell(@"Qizmt exec regression_test_writePASSFAIL.xml regression_test_outputDirection_desc_explicitCache.xml 1");
    }
    else
    {
        Shell(@"Qizmt exec regression_test_writePASSFAIL.xml regression_test_outputDirection_desc_explicitCache.xml 0");
    }
    
    Shell(@"Qizmt del regression_test_outputDirection_desc_explicitCache_cache");
    Shell(@"Qizmt del regression_test_outputDirection_desc_explicitCache_Input*.txt");
    Shell(@"Qizmt del regression_test_outputDirection_desc_explicitCache_Output*.txt");  
    System.IO.File.Delete(f1);
    System.IO.File.Delete(f2);
}

private bool CompareFiles(string f1, string f2)
{
    byte[] buf1 = null;
    byte[] buf2 = null;
    
  buf1 = System.IO.File.ReadAllBytes(f1);
                          
   buf2 = System.IO.File.ReadAllBytes(f2);            
    
    if(buf1.Length != buf2.Length)
    {
        return false;
    }
    
    for(int i = 0; i < buf1.Length; i++)
    {
        if(buf1[i] != buf2[i])
        {
            return false;
        }
    }
    return true;            
}
