Job.Local:

public virtual void Local()
{
    Shell(@"Qizmt del regression_test_recordset.PutInt_Input.txt", true);
    Shell(@"Qizmt del regression_test_recordset.PutInt_Output.txt", true);    
}

Job.Remote:

  public virtual void Remote(RemoteInputStream dfsinput, RemoteOutputStream dfsoutput)
  {             
        //Create sample data.
        int rowCount = 2000;
        char del = ',';
                       
        Random rnd = new Random(System.DateTime.Now.Millisecond / 2 + System.Diagnostics.Process.GetCurrentProcess().Id / 2);
    
        List<byte> onerow = new List<byte>();
        
        for(long rn = 0; rn < rowCount; rn++)
        {
            onerow.Clear();
            
            //int 
            int x = rnd.Next();
            
            if(rnd.Next() % 2 == 1)
            {
                x = x * -1;
            }
            
            string s = x.ToString();
            byte[] buf = System.Text.Encoding.UTF8.GetBytes(s);
            
            foreach(byte b in buf)
            {
                onerow.Add(b);
            }
            
            onerow.Add((byte)del);
            
            //int  
            x = rnd.Next();
            
            if(rnd.Next() % 2 == 1)
            {
                x = x * -1;
            }
            
            s = x.ToString();
            buf = System.Text.Encoding.UTF8.GetBytes(s);
            
            foreach(byte b in buf)
            {
                onerow.Add(b);
            }
            
            dfsoutput.WriteLine(onerow);        
        }        
         
   }

MapReduce.Map:
  
  public virtual void Map(ByteSlice line, MapOutput output)
  {
        string sLine = line.ToString();                
       
        int del = sLine.IndexOf(',');
        
        int d = int.Parse(sLine.Substring(0, del));  
        int d2 = int.Parse(sLine.Substring(del + 1));
       
        recordset key = recordset.Prepare();
        key.PutInt((int)(d % 10));
        
        //testing put
        recordset rs = recordset.Prepare();
        rs.PutInt(d);
        rs.PutInt(d2);
        
        //testing get
        d = rs.GetInt();
        d2 = rs.GetInt();
        
        //testing put
        recordset val = recordset.Prepare();
        val.PutInt(d);
        val.PutInt(d2);
        
        output.Add(key, val);               
  }

MapReduce.Reduce:
  
  public override void Reduce(ByteSlice key, ByteSliceList values, ReduceOutput output)
  {      
        for(int i = 0; i < values.Length; i++)
        {
            recordset rs = recordset.Prepare(values.Items[i]);
            
            //testing get
            int d = rs.GetInt();
            int d2 = rs.GetInt();
            
            recordset val = recordset.Prepare();
            val.PutInt(d);
            val.PutInt(d2);
            
            d = val.GetInt();
            d2 = val.GetInt();
            
            string s = d.ToString() + "," + d2.ToString();
            
            output.Add(ByteSlice.Prepare(s));                    
        }                        
  }

Job.Local:

public virtual void Local()
{
    //Compare input and output file, pass if they are the same.
    Shell(@"Qizmt exec regression_test_checkTestResult.xml regression_test_recordset.PutInt_Input.txt regression_test_recordset.PutInt_Output.txt regression_test_recordset.PutInt.xml");          
    
    Shell(@"Qizmt del regression_test_recordset.PutInt_Input.txt", true);
    Shell(@"Qizmt del regression_test_recordset.PutInt_Output.txt", true); 
}
