Job.Local:

public virtual void Local()
{
    Shell(@"Qizmt del regression_test_remote_output_nonascii_Input.txt");
    Shell(@"Qizmt del regression_test_remote_output_nonascii_Output.txt");
}


public virtual void Local()
{
    string localdir = @"\\" + System.Net.Dns.GetHostName() + @"\c$\temp\qizmt";
    if(!System.IO.Directory.Exists(localdir))
    {
        System.IO.Directory.CreateDirectory(localdir);
    }
    
    string fn = "regression_test_remote_output_nonascii_Input.txt";
    string localfn = localdir + @"\" + Guid.NewGuid().ToString() + fn;
    System.IO.File.WriteAllBytes(localfn, Encoding.UTF8.GetBytes("Non-ASCII '±' character." + Environment.NewLine));
    try
    {
        Shell(@"Qizmt dfs put " + localfn + " " + fn);
    }
    finally
    {
        System.IO.File.Delete(localfn);
    }
}

Job.Remote:

    public virtual void Remote(RemoteInputStream dfsinput, RemoteOutputStream dfsoutput)
    {
        dfsoutput.WriteLine("Non-ASCII '±' character.");
    }

Job.Local:

public virtual void Local()
{
    Shell(@"Qizmt exec regression_test_iocompare.xml regression_test_remote_output_nonascii_Input.txt regression_test_remote_output_nonascii_Output.txt regression_test_remote_output_nonascii.xml");          
    
    Shell(@"Qizmt del regression_test_remote_output_nonascii_Input.txt");
    Shell(@"Qizmt del regression_test_remote_output_nonascii_Output.txt");
}
